// Helper function to get chapters by book
export function getChaptersByBook(bookId: string): ChapterSummary[] {
  switch (bookId) {
    case 'genesis':
      return GENESIS_CHAPTERS;
    case 'exodus':
      return EXODUS_CHAPTERS;
    case 'leviticus':
      return LEVITICUS_CHAPTERS;
    case 'numbers':
      return NUMBERS_CHAPTERS;
    case 'deuteronomy':
      return DEUTERONOMY_CHAPTERS;
    case 'joshua':
      return JOSHUA_CHAPTERS;
    case 'judges':
      return JUDGES_CHAPTERS;
    case 'ruth':
      return RUTH_CHAPTERS;
    case '1-samuel':
      return FIRST_SAMUEL_CHAPTERS;
    case '2-samuel':
      return SECOND_SAMUEL_CHAPTERS;
    case '1-kings':
      return FIRST_KINGS_CHAPTERS;
    case '2-kings':
      return SECOND_KINGS_CHAPTERS;
    case '1-chronicles':
      return FIRST_CHRONICLES_CHAPTERS;
    case '2-chronicles':
      return SECOND_CHRONICLES_CHAPTERS;
    case 'ezra':
      return EZRA_CHAPTERS;
    case 'nehemiah':
      return NEHEMIAH_CHAPTERS;
    case 'esther':
      return ESTHER_CHAPTERS;
    case 'job':
      return JOB_CHAPTERS;
    case 'psalms':
      return PSALMS_CHAPTERS;
    case 'proverbs':
      return PROVERBS_CHAPTERS;
    case 'ecclesiastes':
      return ECCLESIASTES_CHAPTERS;
    case 'song-of-solomon':
      return SONG_OF_SOLOMON_CHAPTERS;
    case 'isaiah':
      return ISAIAH_CHAPTERS;
    case 'jeremiah':
      return JEREMIAH_CHAPTERS;
    case 'lamentations':
      return LAMENTATIONS_CHAPTERS;
    case 'ezekiel':
      return EZEKIEL_CHAPTERS;
    case 'daniel':
      return DANIEL_CHAPTERS;
    case 'hosea':
      return HOSEA_CHAPTERS;
    case 'joel':
      return JOEL_CHAPTERS;
    case 'amos':
      return AMOS_CHAPTERS;
    case 'obadiah':
      return OBADIAH_CHAPTERS;
    case 'jonah':
      return JONAH_CHAPTERS;
    case 'micah':
      return MICAH_CHAPTERS;
    case 'nahum':
      return NAHUM_CHAPTERS;
    case 'habakkuk':
      return HABAKKUK_CHAPTERS;
    case 'zephaniah':
      return ZEPHANIAH_CHAPTERS;
    case 'haggai':
      return HAGGAI_CHAPTERS;
    case 'zechariah':
      return ZECHARIAH_CHAPTERS;
    case 'malachi':
      return MALACHI_CHAPTERS;
    case 'matthew':
      return MATTHEW_CHAPTERS;
    case 'mark':
      return MARK_CHAPTERS;
    case 'luke':
      return LUKE_CHAPTERS;
    case 'john':
      return JOHN_CHAPTERS;
    case 'acts':
      return ACTS_CHAPTERS;
    case '1-corinthians':
      return FIRST_CORINTHIANS_CHAPTERS;
    case '2-corinthians':
      return SECOND_CORINTHIANS_CHAPTERS;
    case 'galatians':
      return GALATIANS_CHAPTERS;
    case 'ephesians':
      return EPHESIANS_CHAPTERS;
    case 'philippians':
      return PHILIPPIANS_CHAPTERS;
    case 'colossians':
      return COLOSSIANS_CHAPTERS;
    // More New Testament books will be added
    default:
      return [];
  }
}

